import scala.collection.mutable.ArrayBuffer
import scala.annotation.tailrec

@tailrec
final def NeumannMethod(i: Int, 
                        probabilityFunction: (Double) => Double, 
                        maxPFValue: Double, 
                        a: Double, 
                        b: Double, 
                        arr: ArrayBuffer[Double]): ArrayBuffer[Double] = {
    if (i == 0) return arr;
    val r1 = random.next
    val r2 = random.next
    val x = a + (b - a) * r1
    if (r2 * maxPFValue < probabilityFunction(x)) NeumannMethod(i - 1, probabilityFunction, maxPFValue, a, b, arr += x)
    else NeumannMethod(i - 1, probabilityFunction, maxPFValue, a, b, arr)
}
