import org.apache.spark.sql.types._
import org.apache.spark.sql.Row

class LinearCongruentGenerator(m: BigInt = BigInt("34359738368"),
                               k: BigInt = BigInt("152587890625"),
                               A0: BigInt = 999) extends Serializable {

  var prevValue: BigInt = A0

  def next: Double = {
    val result: BigInt = (k * prevValue) % m
    prevValue = result
    result.toDouble / m.toDouble
  }
}

val random = new LinearCongruentGenerator

def randomDf(range: Int, random: LinearCongruentGenerator, columnName: String = "value") = {
    spark.createDataFrame(
        spark.sparkContext.parallelize((0 until range).map(i => random.next)).map(t => Row(t)), 
        StructType(
            Seq(
                StructField(columnName, DataTypes.DoubleType, false)
            )
        )
    )
}

