import org.apache.spark.sql.types._
import org.apache.spark.sql.Row
import org.joda.time.DateTime

class LinearCongruentGenerator(m: BigInt = BigInt("34359738368"),
                               k: BigInt = BigInt("152587890625"),
                               A0: BigInt = 9999) extends Serializable {

  var prevValue: BigInt = A0

  def next: Double = {
    val result: BigInt = (k * prevValue) % m
    prevValue = result
    result.toDouble / m.toDouble
  }
}

val random = new LinearCongruentGenerator(A0 = new DateTime().getMillis / 1000)

def randomDf(range: Int, random: LinearCongruentGenerator, columnName: String = "value") = {
    spark.createDataFrame(
        spark.sparkContext.parallelize((0 until range).map(i => random.next)).map(t => Row(t)), 
        StructType(
            Seq(
                StructField(columnName, DataTypes.DoubleType, false)
            )
        )
    )
}

def average(arr: Array[Double]): Double = arr.sum / arr.length

def mean(arr: Array[Double]): Double = average(arr)

def variance(arr: Array[Double]): Double = {
    val sEV = average(arr)
    average(arr.map(i => scala.math.pow(i - sEV, 2)))
}

def median(arr: Array[Double]): Double = {
    val sorted = arr.sortWith(_ < _)
    if (sorted.length % 2 == 0) (arr(sorted.length / 2) + arr(sorted.length / 2 - 1)) / 2
    else arr(sorted.length / 2)
}

def mode(arr: Array[Double], interval: Double): Double = {
    val intervalNumber = arr.groupBy(value => (value / interval).toInt).mapValues(_.size).maxBy(_._2)._1
    (intervalNumber * interval + (intervalNumber + 1) * interval) / 2
}

import breeze.stats.distributions.ChiSquared

// b2 - вероятность ошибки второго рода, то есть что примем неверную
def calcXi2(df: org.apache.spark.sql.DataFrame, M: Double, F: Double => Double, S: Int, b2: Double): (Double, Double, Boolean) = {
    val N = df.count.toDouble
    val (a, b) = (df.select(min('value)).first.getDouble(0), df.select(max('value)).first.getDouble(0))
    val interval = (b - a) / M
    val groupedDf = df.withColumn("int_id", (('value - a) / interval).cast(IntegerType)).filter('int_id < M).groupBy('int_id).agg((count("*") / N).as("pi_"))

    def p(i: Int) = F(a + interval * (i + 1)) - F(a + interval * i)
    val pi = udf(p(_:Int):Double) // Creating udf that will calculate difference of distribution function on ends of i'th interval

    val chi2 = N * groupedDf.agg(sum(pow('pi_ - pi('int_id), 2) / pi('int_id))).first.getDouble(0)
    val boundaryValue = ChiSquared(M - S - 1).inverseCdf(1 - b2)

    (chi2, boundaryValue, chi2 < boundaryValue)
}

val valuesArray = randomDf(100, random).collect.map(_.getDouble(0))
val bitArray = valuesArray.flatMap(i => {
    (i * 100000).round.toBinaryString.map(ch => ch.toInt - 48)
})
val Sobs = scala.math.abs(bitArray.map(i => 2 * i - 1).sum) / scala.math.pow(2 * bitArray.length, 0.5)
println(Sobs)

